import React, { useState } from "react"
import logo from '../../Assets/images/logo.png'
import cancelButton from '../../Assets/images/cross.png'
import './Adduser.css';
import axios from 'axios'
import { useHistory } from "react-router-dom";
import {Link} from 'react-router-dom';
const AddUser = () => {
  let history = useHistory();
  const [user, setUser] = useState({
    name: '',
        address: '',
        city: '',
        state:'',
        zipcode:'',
        phonenumber:'',
        id: '',
        isUpdate: false,
        error_name:'',
        error_zipcode:'',
        error_phonenumber:'',
        }
  );


  const onInputChange =  async event => {
    setUser({ ...user, [event.target.name]: event.target.value });
  };

  const onSubmit = async event => {
    event.preventDefault();
    console.log('user'+user)
    await axios.post("http://localhost:3001/users", user);
    history.push("/");
  };
  const changeValue = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value })
}

const firstnamevalidation = (event)=>{
  const data = event.target.value;
  const Regexp = new RegExp("^[A-Z]{1}[a-z]{2,}$");     
  const test = Regexp.test();
  if(!test){

      setUser((user)=>({
          ...user,
          error_name : 'Not a valid Name'
      }))
  }
  else{
      console.log('else')
      setUser((user)=>({
          ...user,
          error_name : '' 
      }))
  }
}
const phonenumbervalidation = (event)=>{
  const data = event.target.value;
  const Regexp = new RegExp("^[+][1-9]{2}[-][0-9]{10}$");     
