import React, { useState } from "react"
import logo from '../../Assets/images/logo.png'
import cancelButton from '../../Assets/images/cross.png'
import './Adduser.css';
import axios from 'axios'
import { useHistory } from "react-router-dom";
import {Link} from 'react-router-dom';
import { FullName, Zipcode, PhoneNumber } from './Regex';

const AddUser = () => {
  let history = useHistory();
  const [user, setUser] = useState({
    name: '',
        FullName: '',
        Zipcode: '',
        PhoneNumber: '',
        address: '',
        city: '',
        state:'',
        zipcode:'',
        PhoneNumber:'',
        id: '',
        isUpdate: false,
         error: {
        FullName: '',
        Zipcode: '',
        PhoneNumber: '',
        address: '',
        city: '',
        state:'',
        zipcode:'',
        PhoneNumber:'',
        }
  });

  const [Fullname, setFullName] = useState('');
   const [Zipcode, setZipcode] = useState('');
   const [PhoneNumber, setPhoneNumber] = useState('');
   const [FullNameErr, setFullNameErr] = useState(false);
   const [ZipcodeError, setZipcodeError] = useState(false);
   const [PhoneNumberErr, setPhoneNumberError] = useState(false);
   const validate = () => {
      if (!validFullName.test(Fullname)) {
        setFullNameErr(true);
      }
      if (!validZipcode.test(Zipcode)) {
        setZipcodeError(true);
      }
      if (!validPhoneNumber.test(PhoneNumber)) {
        setPhoneNumberError(true);
     }
   };

  const onInputChange =  async event => {
    setUser({ ...user, [event.target.name]: event.target.value });
  };

  const onSubmit = async event => {
    event.preventDefault();
    console.log('user'+user)
    await axios.post("http://localhost:3001/users", user);
    history.push("/");
  };
  const changeValue = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value })

}
  return (  
    <div>
    <input
       type="email"
       placeholder="Email"
       value={email}
       onChange={(e) => setEmail(e.target.value)}
    />
    <input
            type="password"
