import React, { useState ,useEffect} from "react";
import logo from '../../Assets/images/logo.png'
import './Adduser.css';
import axios from 'axios'
import { useHistory, useParams } from "react-router-dom";
const AddUser = () => {
  let history = useHistory();

  const { id } = useParams();
  const [user, setUser] = useState({
    Fullname: '',
       address: '',
        city: '',
        state:'',
        zipcode: '',
        phonenumber:'',

        address: '',
        city: '',
        state:'',
        zipcode: '',
        phonenumber:'',
        errorname:'',
        errorzipcode:'',
        errorphonenumber:'',
  });


  const onInputChange =  async event => {
    setUser({ ...user, [event.target.name]: event.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    await axios.put(`http://localhost:3001/users/${id}`, user);
    history.push("/");
  };

    const changeValue = (event) => {
        setUser({ ...user, [event.target.name]: event.target.value })
        console.log(event.target.value)
    }
    const firstname_validation = (event)=>{
        const data = event.target.value;
        const Regexp = new RegExp("^[A-Z]{1}[a-z]{2,}$");     
        const test = Regexp.test(data);
        if(!test){

            setUser((user)=>({
                ...user,
                errorname : 'Not a valid Name'
            }))
        }
        else{
            console.log('else')
            setUser((user)=>({
                ...user,
                errorname : ''
            }))
        }
    }
    const phonenumber_validation = (event)=>{
        const data = event.target.value;
        const Regexp = new RegExp("^[+][1-9]{2}[-][0-9]{10}$");     
        const test = Regexp.test(data);
        if(!test){

            setUser((user)=>({
                ...user,
                errorphonenumber : 'Not a valid Number'
            }))
        }
        else{
            console.log('else')
            setUser((user)=>({
                ...user,
                errorphonenumber : ''
            }))
        }
    }
    const zipcode_validation = (event)=>{
        const data = event.target.value;
        const Regexp = new RegExp("^[0-9]{5}(?:-[0-9]{4})?$");     
        const test = Regexp.test(data);
        if(!test){

            setUser((user)=>({
                ...user,
                errorzipcode: 'Enter valid zip-code'
            }))
        }
        else{
            console.log('else')
            setUser((user)=>({
                ...user,
                errorzipcode : ''
            }))
        }
    }
    const loadUser = async () => {
        const result = await axios.get(`http://localhost:3001/users/${id}`);
        setUser(result.data);
    };
    useEffect(()=>{
